# Get the lists from the FIRST item (since Aggregate merges everything into one)
input_json = _input.first().json

# Access the arrays
# Make sure your Aggregate node actually named them "pageContent" and "score"
contents = input_json['pageContent']
scores = input_json['score']

MIN_SCORE = 0.50

# Filter logic: Zip them together, check score, keep content
relevant_texts = [
    content for content, score in zip(contents, scores)
    if score >= MIN_SCORE
]

# Handle Empty Case
if not relevant_texts:
    return [{
        "json": {
            "found_laws": False,
            "laws_text": contents[:10]
        }
    }]

# Combine them into one big string for the LLM
combined_text = "\n---\n".join(relevant_texts)

# Return standard n8n format
return [{
    "json": {
        "found_laws": True,
        "laws_text": combined_text
    }
}]

